"use strict";

var complain;

module.exports = function forRangeHelper(from, to, step, callback) {
  if (step == null) {
    step = from <= to ? 1 : -1;

    // eslint-disable-next-line no-constant-condition
  }

  var currentStep = 0;
  var totalSteps = (to - from) / step;

  while (currentStep <= totalSteps) {
    callback(from + currentStep++ * step);
  }
};